import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

export default function Dashboard() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [selectedBorough, setSelectedBorough] = useState('All');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://127.0.0.1:5000/api/violations');
        if (!response.ok) throw new Error('Network response was not ok');
        const json = await response.json();
        setData(json.violations);
        setFilteredData(json.violations);
      } catch (err) {
        setError('Failed to load data. Make sure the Flask backend is running.');
        console.error('Error fetching data:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    if (selectedBorough === 'All') {
      setFilteredData(data);
    } else {
      setFilteredData(data.filter((item) => item.borough === selectedBorough));
    }
  }, [selectedBorough, data]);

  const boroughs = ['All', ...Array.from(new Set(data.map((item) => item.borough)))]

  const chartData = {
    labels: filteredData.map((item) => item.law_firm),
    datasets: [
      {
        label: 'Number of Violations',
        data: filteredData.map((item) => item.violations),
        backgroundColor: 'rgba(239, 68, 68, 0.8)',
        borderColor: 'rgba(239, 68, 68, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: `Legal Violations by Law Firm${selectedBorough !== 'All' ? ` - ${selectedBorough}` : ''}`,
        font: {
          size: 16,
        },
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 1,
        },
      },
      x: {
        ticks: {
          maxRotation: 45,
          minRotation: 45,
        },
      },
    },
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <h1 className="text-2xl font-bold text-gray-900">NYC Legal Violations Dashboard</h1>
          <p className="text-gray-600 mt-1">Monitoring potential legal violations by law firms</p>
        </div>
      </header>
      <main className="flex-1 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white rounded-lg shadow-sm border p-6">
          <div className="mb-6 flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
            <div className="flex items-center gap-2">
              <label htmlFor="borough-select" className="text-sm font-medium text-gray-700">
                Filter by Borough:
              </label>
              <select
                id="borough-select"
                value={selectedBorough}
                onChange={(e) => setSelectedBorough(e.target.value)}
                className="border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-red-500 focus:border-red-500"
              >
                {boroughs.map((borough) => (
                  <option key={borough} value={borough}>
                    {borough}
                  </option>
                ))}
              </select>
            </div>
            <div className="text-sm text-gray-600">Showing {filteredData.length} law firms</div>
          </div>
          <div className="h-96 w-full">
            {loading ? (
              <div className="flex items-center justify-center h-full">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-red-500"></div>
                <div className="ml-3 text-gray-500">Loading data...</div>
              </div>
            ) : error ? (
              <div className="flex items-center justify-center h-full">
                <div className="text-red-500 text-center">
                  <p className="font-medium">Error loading data</p>
                  <p className="text-sm mt-1">{error}</p>
                </div>
              </div>
            ) : filteredData.length === 0 ? (
              <div className="flex items-center justify-center h-full">
                <div className="text-gray-500">No data available for selected borough</div>
              </div>
            ) : (
              <Bar data={chartData} options={chartOptions} />
            )}
          </div>
          {!loading && !error && filteredData.length > 0 && (
            <div className="mt-6 grid grid-cols-1 sm:grid-cols-3 gap-4">
              <div className="bg-gray-50 rounded-lg p-4 text-center">
                <div className="text-2xl font-bold text-gray-900">{filteredData.length}</div>
                <div className="text-sm text-gray-600">Law Firms</div>
              </div>
              <div className="bg-gray-50 rounded-lg p-4 text-center">
                <div className="text-2xl font-bold text-red-600">
                  {filteredData.reduce((sum, item) => sum + item.violations, 0)}
                </div>
                <div className="text-sm text-gray-600">Total Violations</div>
              </div>
              <div className="bg-gray-50 rounded-lg p-4 text-center">
                <div className="text-2xl font-bold text-orange-600">
                  {Math.round(
                    (filteredData.reduce((sum, item) => sum + item.violations, 0) / filteredData.length) * 10,
                  ) / 10}
                </div>
                <div className="text-sm text-gray-600">Avg per Firm</div>
              </div>
            </div>
          )}
        </div>
      </main>
      <footer className="bg-white border-t">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <p className="text-center text-sm text-gray-600">
            Â© 2024 NYC Legal Violations Dashboard - MVP Demo for JuriSee
          </p>
        </div>
      </footer>
    </div>
  );
}
